    if i % 3 == 0:
        print("San!")
    elif '3' in str(i):
        print("San!")  # 同じ処理
    else:
        print(i)

型変換を行わない実装例
def contains_three(n):
    while n >= 3: # 3 未満は 3 を含まないので終了
        if n - n//10 * 10 == 3:
            return True  # 下一桁が 3 である場合は True を返して終了
        n //= 10  # 下一桁を削って (10 で割って切り捨て) 処理を継続
    return False

for i in range(1, 101):
    if i % 3 == 0 or contains_three(i):
        print("San!")
    else:
        print(i)

